import React, { useEffect, useState } from "react";



export default function AddWords() {
    const [adjective, setAdjective] = useState(null)
    const [noun, setNoun] = useState(null)
    const [newWord, setNewWord] = useState("")
    const [sendReady, setSendReady] = useState(false)


const makePost = () => {
    fetch("http://localhost:5002/namelist", {

    // fetch("http://namelist.herokuapp.com/namelist", {
        method: "POST",
        header: { "Content-Type": "application/json" },
        // body:  JSON.stringify(newWord)
        body: <h1>hello</h1>
      }).then(() => {
        console.log("new blog added", JSON.stringify(newWord));
      });
    };


    useEffect(() => {
       if (sendReady) {
          makePost(); 
       } 
    }, [sendReady])

    return (
        <div className="container">
                <h4> I want to add: </h4>

<div className="buttonAddBox">    
    <div onClick={() => {setAdjective(true, setNoun(false))}}> <div className={(adjective || adjective === null) ? "buttonAdd" : "buttonAddNo"}>an adjective</div></div>
    <div onClick={() => {setNoun(true, setAdjective(false))}}> <div className={(noun || noun === null) ? "buttonAdd" : "buttonAddNo"}>a noun</div></div>
</div>
<form
onSubmit={(e) => {e.preventDefault(); setSendReady(true)}}>
<input  onChange={(e) => setNewWord(e.target.value)} placeholder="Enter <adjective> here"></input>
<input type="submit"></input>
</form>
{newWord}

        </div>
    )
}
